define({"topics" : [{"title":"Spark Versions and Stage Libraries","href":"datacollector\/UserGuide\/Processors\/Spark.html#concept_nbj_1jb_c1b","attributes": {"data-id":"concept_nbj_1jb_c1b",},"menu": {"hasChildren":false,},"tocID":"concept_nbj_1jb_c1b-d46e75044","topics":[]},{"title":"Standalone Pipelines","href":"datacollector\/UserGuide\/Processors\/Spark.html#concept_hpm_tns_hz","attributes": {"data-id":"concept_hpm_tns_hz",},"menu": {"hasChildren":false,},"tocID":"concept_hpm_tns_hz-d46e75066","topics":[]},{"title":"Cluster Pipelines","shortdesc":"\n               <p class=\"shortdesc\">You can use the Spark Evaluator processor in cluster pipelines that process data from         a Kafka or MapR cluster in cluster\n                  streaming mode. You cannot use the processor in cluster         pipelines that process data from a MapR cluster in cluster\n                  batch mode or from HDFS. \n               </p>\n            ","href":"datacollector\/UserGuide\/Processors\/Spark.html#concept_b1l_5lv_jz","attributes": {"data-id":"concept_b1l_5lv_jz",},"menu": {"hasChildren":false,},"tocID":"concept_b1l_5lv_jz-d46e75098","topics":[]},{"title":"Developing the Spark Application","href":"datacollector\/UserGuide\/Processors\/Spark.html#concept_lfl_dvd_1y","attributes": {"data-id":"concept_lfl_dvd_1y",},"menu": {"hasChildren":false,},"tocID":"concept_lfl_dvd_1y-d46e75143","topics":[]},{"title":"Installing the Application","shortdesc":"\n               <p class=\"shortdesc\">Install the Spark application JAR file as an external library for <span class=\"ph\">Data Collector</span>. If         your custom Spark application depends on external libraries other than the <span class=\"ph\">streamsets-datacollector-api,                         streamsets-datacollector-spark-api, and spark-core libraries</span>, install those libraries in the same location as well. \n               </p>\n            ","href":"datacollector\/UserGuide\/Processors\/Spark.html#task_dr2_gvd_1y","attributes": {"data-id":"task_dr2_gvd_1y",},"menu": {"hasChildren":false,},"tocID":"task_dr2_gvd_1y-d46e75195","topics":[]},{"title":"Configuring a Spark Evaluator","href":"datacollector\/UserGuide\/Processors\/Spark.html#task_g1p_gqn_zx","attributes": {"data-id":"task_g1p_gqn_zx",},"menu": {"hasChildren":false,},"tocID":"task_g1p_gqn_zx-d46e75267","topics":[]}]});