define({"topics" : [{"title":"Configuring LDAP Authentication","shortdesc":"\n               <p class=\"shortdesc\">If your organization uses LDAP, configure <span class=\"ph\">Data Collector</span> to use         LDAP authentication. After you configure LDAP authentication, users log in to <span class=\"ph\">Data Collector</span> using         their LDAP username and password. \n               </p>\n            ","href":"datacollector\/UserGuide\/Configuration\/Authentication.html#concept_x2j_5ts_g5","attributes": {"data-id":"concept_x2j_5ts_g5",},"menu": {"hasChildren":true,},"tocID":"concept_x2j_5ts_g5-d46e6644","next":"concept_x2j_5ts_g5-d46e6644",},{"title":"Configuring File-Based Authentication","shortdesc":"\n               <p class=\"shortdesc\">If your organization does not use LDAP, configure <span class=\"ph\">Data Collector</span> to use         the default file-based authentication. \n               </p>\n            ","href":"datacollector\/UserGuide\/Configuration\/Authentication.html#concept_wgy_rxt_5x","attributes": {"data-id":"concept_wgy_rxt_5x",},"menu": {"hasChildren":true,},"tocID":"concept_wgy_rxt_5x-d46e7148","next":"concept_wgy_rxt_5x-d46e7148",}]});