define({"topics" : [{"title":"Cluster Batch and Streaming Execution Modes","shortdesc":"\n               <p class=\"shortdesc\"><span class=\"ph\">Data Collector</span>         can run a cluster pipeline using cluster batch or cluster streaming execution         mode.\n               </p>\n            ","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_rjc_4m5_lx","attributes": {"data-id":"concept_rjc_4m5_lx",},"menu": {"hasChildren":false,},"tocID":"concept_rjc_4m5_lx-d46e117970","topics":[]},{"title":"Data Collector Configuration","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_ywt_vp3_vdb","attributes": {"data-id":"concept_ywt_vp3_vdb",},"menu": {"hasChildren":false,},"tocID":"concept_ywt_vp3_vdb-d46e117997","topics":[]},{"title":"Enable HTTPS","shortdesc":"\n               <p class=\"shortdesc\">You can enable <span class=\"ph\">Data Collector</span>to use         HTTPS when you run cluster pipelines. By default <span class=\"ph\">Data Collector</span> uses         HTTP.\n               </p>\n            ","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_rmd_hgp_cw","attributes": {"data-id":"concept_rmd_hgp_cw",},"menu": {"hasChildren":false,},"tocID":"concept_rmd_hgp_cw-d46e118029","topics":[]},{"title":"Logs","shortdesc":"\n               <p class=\"shortdesc\">Because cluster pipelines run as either MapReduce or Spark applications, each <span class=\"ph\">Data Collector</span> worker         in the cluster manages its own log. \n               </p>\n            ","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_iyx_23c_j2b","attributes": {"data-id":"concept_iyx_23c_j2b",},"menu": {"hasChildren":false,},"tocID":"concept_iyx_23c_j2b-d46e118080","topics":[]},{"title":"Checkpoint Storage for Streaming Pipelines","shortdesc":"\n               <p class=\"shortdesc\">When the <span class=\"ph\">Data Collector</span> runs a         cluster streaming pipeline, on either Mesos or YARN, the <span class=\"ph\">Data Collector</span>         generates and stores checkpoint metadata. The checkpoint metadata provides the offset for         the origin.\n               </p>\n            ","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_cs4_lcg_j5","attributes": {"data-id":"concept_cs4_lcg_j5",},"menu": {"hasChildren":true,},"tocID":"concept_cs4_lcg_j5-d46e118138","next":"concept_cs4_lcg_j5-d46e118138",},{"title":"Error Handling Limitations","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_xxz_nft_ls","attributes": {"data-id":"concept_xxz_nft_ls",},"menu": {"hasChildren":false,},"tocID":"concept_xxz_nft_ls-d46e118289","topics":[]},{"title":"Monitoring and Snapshot","href":"datacollector\/UserGuide\/Cluster_Mode\/ClusterPipelines.html#concept_fk4_gd4_1s","attributes": {"data-id":"concept_fk4_gd4_1s",},"menu": {"hasChildren":false,},"tocID":"concept_fk4_gd4_1s-d46e118372","topics":[]}]});