define({"topics" : [{"title":"Java Heap Size","shortdesc":"\n                  <p class=\"shortdesc\">Increase or decrease the <span class=\"ph\">Data Collector</span> Java heap     size as necessary, based on the resources available on the host machine. By default, the Java     heap size\n                     is 1024 MB. \n                  </p>\n               ","href":"datacollector\/UserGuide\/Configuration\/DCEnvironmentConfig.html#concept_mdc_shg_qr","attributes": {"data-id":"concept_mdc_shg_qr",},"menu": {"hasChildren":false,},"tocID":"concept_mdc_shg_qr-d46e9927","topics":[]},{"title":"Remote Debugging","shortdesc":"\n                  <p class=\"shortdesc\">You can enable remote debugging to debug a <span class=\"ph\">Data Collector</span>         instance running on a remote machine. \n                  </p>\n               ","href":"datacollector\/UserGuide\/Configuration\/DCEnvironmentConfig.html#concept_wk1_pyw_4x","attributes": {"data-id":"concept_wk1_pyw_4x",},"menu": {"hasChildren":false,},"tocID":"concept_wk1_pyw_4x-d46e9985","topics":[]},{"title":"Garbage Collector","shortdesc":"\n                  <p class=\"shortdesc\">You can define the Java garbage collector that <span class=\"ph\">Data Collector</span> uses.         By default, <span class=\"ph\">Data Collector</span> uses         the Concurrent Mark Sweep (CMS) garbage collector.\n                  </p>\n               ","href":"datacollector\/UserGuide\/Configuration\/DCEnvironmentConfig.html#concept_kqh_lj3_vx","attributes": {"data-id":"concept_kqh_lj3_vx",},"menu": {"hasChildren":false,},"tocID":"concept_kqh_lj3_vx-d46e10054","topics":[]}]});