define({"topics" : [{"title":"Field Mappings","shortdesc":"\n               <p class=\"shortdesc\">When you configure the MapR DB destination, you map fields from records to MapR DB         columns.</p>\n            ","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_tr1_143_1w","attributes": {"data-id":"concept_tr1_143_1w",},"menu": {"hasChildren":false,},"tocID":"concept_tr1_143_1w-d46e88704","topics":[]},{"title":"Time Basis","shortdesc":"\n               <p class=\"shortdesc\">The time basis determines the timestamp value added for each column written to MapR DB. </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_qxm_hq3_1w","attributes": {"data-id":"concept_qxm_hq3_1w",},"menu": {"hasChildren":false,},"tocID":"concept_qxm_hq3_1w-d46e88729","topics":[]},{"title":"Kerberos Authentication","shortdesc":"\n               <p class=\"shortdesc\">You can use Kerberos authentication to connect to MapR DB. When you use Kerberos         authentication, <span class=\"ph\">Data Collector</span> uses         the Kerberos principal and keytab to connect to MapR DB. By default, <span class=\"ph\">Data Collector</span> uses         the user account who started it to connect.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_fvq_r43_1w","attributes": {"data-id":"concept_fvq_r43_1w",},"menu": {"hasChildren":false,},"tocID":"concept_fvq_r43_1w-d46e88764","topics":[]},{"title":"Using an HBase User","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_hv2_4p3_1w","attributes": {"data-id":"concept_hv2_4p3_1w",},"menu": {"hasChildren":false,},"tocID":"concept_hv2_4p3_1w-d46e88815","topics":[]},{"title":"HDFS Properties and Configuration File","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#concept_fth_vq3_1w","attributes": {"data-id":"concept_fth_vq3_1w",},"menu": {"hasChildren":false,},"tocID":"concept_fth_vq3_1w-d46e88867","topics":[]},{"title":"Configuring a MapR DB Destination","href":"datacollector\/UserGuide\/Destinations\/MapRDB.html#task_pgk_p2z_yv","attributes": {"data-id":"task_pgk_p2z_yv",},"menu": {"hasChildren":false,},"tocID":"task_pgk_p2z_yv-d46e88929","topics":[]}]});