define({"topics" : [{"title":"Partition Strategy","shortdesc":"\n                  <p class=\"shortdesc\">The partition strategy determines how to write statistics to Kafka partitions. You can         use a partition strategy to\n                     balance the work load or to write data semantically.\n                  </p>\n               ","href":"datacollector\/UserGuide\/DPM\/AggregatedStatistics.html#concept_wph_j2b_fx","attributes": {"data-id":"concept_wph_j2b_fx",},"menu": {"hasChildren":false,},"tocID":"concept_wph_j2b_fx-d46e122543","topics":[]},{"title":"Enabling Security","shortdesc":"\n                  <p class=\"shortdesc\">When you write statistics to a Kafka cluster, you can configure the pipeline to         connect securely to Kafka through\n                     SSL\/TLS, Kerberos, or both.\n                  </p>\n               ","href":"datacollector\/UserGuide\/DPM\/AggregatedStatistics.html#concept_ktn_ql3_cgb","attributes": {"data-id":"concept_ktn_ql3_cgb",},"menu": {"hasChildren":false,},"tocID":"concept_ktn_ql3_cgb-d46e122609","topics":[]},{"title":"Best Practices for a Kafka Cluster","href":"datacollector\/UserGuide\/DPM\/AggregatedStatistics.html#concept_bmq_ff5_xw","attributes": {"data-id":"concept_bmq_ff5_xw",},"menu": {"hasChildren":false,},"tocID":"concept_bmq_ff5_xw-d46e122685","topics":[]}]});