define({"topics" : [{"title":"Field Mappings","shortdesc":"\n               <p class=\"shortdesc\">When you configure the HBase destination, you map fields from records to HBase         columns.</p>\n            ","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_vn5_cr5_4v","attributes": {"data-id":"concept_vn5_cr5_4v",},"menu": {"hasChildren":false,},"tocID":"concept_vn5_cr5_4v-d46e84000","topics":[]},{"title":"Kerberos Authentication","shortdesc":"\n               <p class=\"shortdesc\">You can use Kerberos authentication to connect to HBase. When you use Kerberos   authentication, <span class=\"ph\">Data Collector</span> uses the   Kerberos principal and keytab to connect to HBase. By default, <span class=\"ph\">Data Collector</span> uses the user   account who started it to connect.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_xy5_4tm_vs","attributes": {"data-id":"concept_xy5_4tm_vs",},"menu": {"hasChildren":false,},"tocID":"concept_xy5_4tm_vs-d46e84025","topics":[]},{"title":"Using an HBase User","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_gbt_fpt_ls","attributes": {"data-id":"concept_gbt_fpt_ls",},"menu": {"hasChildren":false,},"tocID":"concept_gbt_fpt_ls-d46e84066","topics":[]},{"title":"Time Basis","shortdesc":"\n               <p class=\"shortdesc\">The time basis determines the timestamp value added for each column written to HBase. </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_t4g_vc2_wv","attributes": {"data-id":"concept_t4g_vc2_wv",},"menu": {"hasChildren":false,},"tocID":"concept_t4g_vc2_wv-d46e84108","topics":[]},{"title":"HDFS Properties and Configuration File","href":"datacollector\/UserGuide\/Destinations\/HBase.html#concept_tjp_v5l_zq","attributes": {"data-id":"concept_tjp_v5l_zq",},"menu": {"hasChildren":false,},"tocID":"concept_tjp_v5l_zq-d46e84163","topics":[]},{"title":"Configuring an HBase Destination","href":"datacollector\/UserGuide\/Destinations\/HBase.html#task_pyq_qx5_vr","attributes": {"data-id":"task_pyq_qx5_vr",},"menu": {"hasChildren":false,},"tocID":"task_pyq_qx5_vr-d46e84225","topics":[]}]});