define({"topics" : [{"title":"Index Mode","shortdesc":"\n               <p class=\"shortdesc\">The index mode determines how the Solr destination indexes records when writing to Solr.   Index mode also determines how\n                  the destination handles errors. \n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Solr.html#concept_z4y_bgr_wr","attributes": {"data-id":"concept_z4y_bgr_wr",},"menu": {"hasChildren":false,},"tocID":"concept_z4y_bgr_wr-d46e92394","topics":[]},{"title":"Kerberos Authentication","shortdesc":"\n               <p class=\"shortdesc\">You can use Kerberos authentication to connect to a Solr node or cluster. When you use         Kerberos authentication, <span class=\"ph\">Data Collector</span> uses         the Kerberos principal and keytab to connect to Solr. \n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Solr.html#concept_npl_fss_zw","attributes": {"data-id":"concept_npl_fss_zw",},"menu": {"hasChildren":false,},"tocID":"concept_npl_fss_zw-d46e92419","topics":[]},{"title":"Configuring a Solr Destination","href":"datacollector\/UserGuide\/Destinations\/Solr.html#task_ld1_phr_wr","attributes": {"data-id":"task_ld1_phr_wr",},"menu": {"hasChildren":false,},"tocID":"task_ld1_phr_wr-d46e92457","topics":[]}]});