define({"topics" : [{"title":"Prerequisites","href":"datacollector\/UserGuide\/Destinations\/Splunk.html#concept_xrt_ynm_d2b","attributes": {"data-id":"concept_xrt_ynm_d2b",},"menu": {"hasChildren":false,},"tocID":"concept_xrt_ynm_d2b-d46e92565","topics":[]},{"title":"Required Record Format","shortdesc":"\n               <p class=\"shortdesc\">Splunk requires that the event data and metadata be correctly formatted in the         record. If the record is formatted\n                  incorrectly, an error occurs and the destination fails to         write to Splunk. When you design a pipeline with the Splunk\n                  destination, you must ensure         that the record sent to the destination uses the required format.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Splunk.html#concept_jvc_nzm_d2b","attributes": {"data-id":"concept_jvc_nzm_d2b",},"menu": {"hasChildren":false,},"tocID":"concept_jvc_nzm_d2b-d46e92587","topics":[]},{"title":"Logging Request and Response Data","shortdesc":"\n               <p class=\"shortdesc\">The Splunk destination can log request and response data to the <span class=\"ph\">Data Collector</span>         log.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Splunk.html#concept_hgj_ytm_d2b","attributes": {"data-id":"concept_hgj_ytm_d2b",},"menu": {"hasChildren":false,},"tocID":"concept_hgj_ytm_d2b-d46e92622","topics":[]},{"title":"Configuring a Splunk Destination","href":"datacollector\/UserGuide\/Destinations\/Splunk.html#task_rc1_wqn_xdb","attributes": {"data-id":"task_rc1_wqn_xdb",},"menu": {"hasChildren":false,},"tocID":"task_rc1_wqn_xdb-d46e92670","topics":[]}]});